section.para:
            Az elkészített hangyaszimulációs programunk több részre van osztályokkal felbontva, melyeknek több különböző szerepe
            van. Először is a legalapabb része a hangya kolóniánknak egy individuális hangya, mely az Ant
            osztályba lett létrehozva és amely mindössze csak három adattal rendelkezik: a hangya x,y koordinátája és az iránya.
            #ifndef ANT_H
#define ANT_H

class Ant
{
public:
    int x;
    int y;
    int dir;
    Ant(int x, int y): x(x), y(y) {

        dir = qrand() % 8;

    }
};

typedef std::vector<Ant> Ants; 

            Ezt az osztályt használja fel a programunk a továbbiakban, hogy a hangyákat elkészítse. Rendelkezünk még egy
            AntWin osztállyal, amely tartalmazza konstruktort, dekonstruktort és a rajzolási eventet.
            #ifndef ANTWIN_H
#define ANTWIN_H

#include <QMainWindow>
#include <QPainter>
#include <QString>
#include <QCloseEvent>
#include "antthread.h"
#include "ant.h"

class AntWin : public QMainWindow
{
    Q_OBJECT

public:
    AntWin(int width = 100, int height = 75,
           int delay = 120, int numAnts = 100,
           int pheromone = 10, int nbhPheromon = 3,
           int evaporation = 2, int cellDef = 1,
           int min = 2, int max = 50,
           int cellAntMax = 4, QWidget *parent = 0);

    AntThread* antThread;

    void closeEvent ( QCloseEvent *event ) {

        antThread->finish();
        antThread->wait();
        event->accept();
    }

    void keyPressEvent ( QKeyEvent *event )
    {

        if ( event->key() == Qt::Key_P ) {
            antThread->pause();
        } else if ( event->key() == Qt::Key_Q
                    ||  event->key() == Qt::Key_Escape ) {
            close();
        }

    }

    virtual ~AntWin();
    void paintEvent(QPaintEvent*);

private:

    int ***grids;
    int **grid;
    int gridIdx;
    int cellWidth;
    int cellHeight;
    int width;
    int height;
    int max;
    int min;
    Ants* ants;

public slots :
    void step ( const int &);
};
            Az utolsó osztályunk az AntThread, melyet a hangyák mozgatására használunk, ebbe bele tartozik
            az, hogy új irányt adunk nekik, konkrétan elmozgatjuk őket és a feromon kibocsátásukat is meghatározzuk.
            #ifndef ANTTHREAD_H
#define ANTTHREAD_H
#include <QThread>
#include "ant.h"

class AntThread : public QThread
{
    Q_OBJECT

public:
    AntThread(Ants * ants, int ***grids, int width, int height,
             int delay, int numAnts, int pheromone, int nbrPheromone, 
             int evaporation, int min, int max, int cellAntMax);

    ~AntThread();

    void run();
    void finish()
    {
        running = false;
    }

    void pause()
    {
        paused = !paused;
    }

    bool isRunnung()
    {
        return running;
    }

private:
    bool running {true};
    bool paused {false};
    Ants* ants;
    int** numAntsinCells;
    int min, max;
    int cellAntMax;
    int pheromone;
    int evaporation;
    int nbrPheromone;
    int ***grids;
    int width;
    int height;
    int gridIdx;
    int delay;

    void timeDevel();

    int newDir(int sor, int oszlop, int vsor, int voszlop);
    void detDirs(int irany, int& ifrom, int& ito, int& jfrom, int& jto );
    int moveAnts(int **grid, int row, int col, int& retrow, int& retcol, int);
    double sumNbhs(int **grid, int row, int col, int);
    void setPheromone(int **grid, int row, int col);

signals:
    void step ( const int &);

};
